CREATE TABLE "channel" (name TEXT PRIMARY KEY, description TEXT, settings TEXT, type INTEGER, encoder TEXT REFERENCES "encoder" (name) ON DELETE CASCADE ON UPDATE CASCADE);
CREATE TABLE "encoder" (name TEXT PRIMARY KEY, audio_bitrate INTEGER, audio_encoder TEXT, audio_format INTEGER, audio_source TEXT, video_bitrate INTEGER, video_buffer INTEGER, video_encoder TEXT, video_preset INTEGER, video_quality INTEGER);
CREATE TABLE "fps" (name TEXT PRIMARY KEY, fps INTEGER, enabled INTEGER);
CREATE TABLE "property" (name TEXT PRIMARY KEY, value TEXT);
CREATE TABLE "resolution" (name TEXT PRIMARY KEY, width INTEGER, height INTEGER, enable INTEGER);
CREATE TABLE "scale" (name TEXT PRIMARY KEY, scale TEXT, enabled INTEGER);
CREATE TABLE "scene" (name TEXT PRIMARY KEY, enabled INTEGER);
CREATE TABLE "surface" (name TEXT PRIMARY KEY, x_pos REAL, y_pos REAL, x_scale REAL, y_scale REAL, x_angle REAL, y_angle REAL, z_angle REAL, left_cut REAL, right_cut REAL, top_cut REAL, bottom_cut REAL, keep_aspect REAL, red REAL, green REAL, blue REAL, alfa REAL, contrast REAL, brightness REAL, red_chroma REAL, green_chroma REAL, blue_chroma REAL, red_tolerance_chroma REAL, blue_tolerance_chroma REAL, green_tolerance_chroma REAL, chroma_enabled REAL, capture_name TEXT, capture_device_id INTEGER, scene TEXT REFERENCES "scene" (name) ON DELETE CASCADE ON UPDATE CASCADE);
